      &lt;div class=&quot;col-md-4 ms-auto&quot;&gt;.col-md-4 .ms-auto&lt;/div&gt;
    &lt;/div&gt;
    &lt;div class=&quot;row&quot;&gt;
      &lt;div class=&quot;col-md-3 ms-auto&quot;&gt;.col-md-3 .ms-auto&lt;/div&gt;
